import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';

@Component({
  selector: 'app-eventos',
  templateUrl: './eventos.component.html',
  styleUrls: ['./eventos.component.scss']
})
export class EventosComponent {

  constructor(private http: HttpClient){}

  ngOnInit():void{ //atribui o metodo geteventos
    this.GetEventos();
  }

  public eventos:any = [];
  public eventosFiltrados:any =[];
  largura: number = 150;
  margem: number =2;
  mostrarImagem: boolean = true;
  private _filtroLista: string = '';

  public get filtroLista(){
    return this._filtroLista;
  }

  filtrarEventos(filtrarPor: string): any{
    filtrarPor = filtrarPor.toLocaleLowerCase();
    return this.eventos.filter(
      (      evento: { tema: string; local: string; }) => evento.tema.toLocaleLowerCase().indexOf(filtrarPor)!== -1 ||
      evento.local.toLocaleLowerCase().indexOf(filtrarPor)!== -1 ||
      evento.tema.toLocaleLowerCase().indexOf(filtrarPor)!== -1 
    );
  }


  public set filtroLista(value:string){
     this._filtroLista = value;
     this.eventosFiltrados = this.filtroLista ? this.filtrarEventos(this.filtroLista) : this.eventos;


  }

  alterarImagem(){
    this.mostrarImagem = !this.mostrarImagem;
  }

  public GetEventos():void{ //metodo criado para os dados
      this.http.get('https://localhost:5001/api/eventos').subscribe(
        response => {
          this.eventos = response;
          this.eventosFiltrados = this.eventos;
        },
        error => console.log(error)

      );


  }

}
<h2 class="text-success m-4"><b>Eventos</b></h2>
<hr>

<div class="">
  <div class="form-group mb-2   d-flex ">
    <label for="search" class="sr-onlyc m-2 p-4"><b>Filtro:</b></label>
    <input type="text"
    class="form-control  m-2 sm"
    style="max-width: max-content;"
    placeholder="Buscar"
    [(ngModel)]="filtroLista"
    >
  </div>
</div>
<!-- <h3>Filtro: {{filtroLista}}</h3> -->
<div class="table-responsive ">
  <table class="table table-striped ">
    <thead class=" text-center ">
      <tr class="text-center ">
        <th class="bg-dark text-white">
          <button class="btn btn-outline-light btn-sm"
          (click)="alterarImagem()">
            {{!mostrarImagem ? "EXIBIR" : "OCULTAR"}}
          </button>
        </th>
        <th class="bg-dark text-white" >ID</th>
        <th class="bg-dark text-white">TEMA</th>
        <th class="bg-dark text-white">LOCAL</th>
        <th class="bg-dark text-white">DATA</th>
        <th class="bg-dark text-white">QUANTIDADE DE <br> PESSOAS</th>
        <th class="bg-dark text-white">LOTE</th>
        <th class="bg-dark text-white">OPÇÕES</th>
      </tr>
    </thead>
    <tbody *ngIf="eventos && eventos.length ">
      <tr *ngFor="let evento of eventosFiltrados" class="text-center">
        <td>
          <img *ngIf="mostrarImagem"

          src="/Front/Evento-App/src/assets/{{evento.imagemUrl}}"
          [style.width.px]="largura"
          [style.margin.px]="margem">
        </td>
        <td>{{evento.id}}</td>
        <td>{{evento.tema}}</td>
        <td>{{evento.local}}</td>
        <td>{{evento.dataEvento}}</td>
        <td>{{evento.qtdPessoas}}</td>
        <td>{{evento.lote}}</td>
        <td></td>
      </tr>
    </tbody>
    <tfoot *ngIf="!eventos.length ">
      <tr>
        <td colspan="8">
          <h4 class="text-center">Nenhum Evento Encontrado!!!</h4>
        </td>
      </tr>
    </tfoot>
  </table>
</div>
